import React, { useState } from 'react';
import MissionRow from './MissionRow';
import './MissionLog.scss';

const rce = React.createElement;

/* props
* campaignName
* finale
*/
function MissionLog(props) {
	//const missions = props.missions.map((mission) => rce('li', null, rce(MissionRow, mission)));

	const[missions, setMissions] = useState([
		{
			missionType: 'Story Mission',
			missionTitle: 'Out of the Frying Pan',
			threatLevel: 2,
			missionAccomplished: true,
			itemTiers: [1],
			rebelUpgradeComplete: true,
			imperialUpgradeComplete: true,
		},
		{
			missionType: 'Side Mission',
			missionTitle: 'Into the Fire',
			threatLevel: 3,
			missionAccomplished: true,
			itemTiers: [1, 2],
			rebelUpgradeComplete: false,
			imperialUpgradeComplete: false
		},
		{
			missionType: 'story mission',
			missionTitle: 'Dark Days',
			threatLevel: 4,
			missionAccomplished: false,
			itemTiers: [2],
			rebelUpgradeComplete: false,
			imperialUpgradeComplete: false,
		},
		{
			missionType: 'side mission',
			missionTitle: '',
			threatLevel: 5,
			missionAccomplished: false,
			itemTiers: [2, 3],
			rebelUpgradeComplete: false,
			imperialUpgradeComplete: false
		},
	]);

	function handleChange(idx, key, value) {
		let missionsClone = missions.slice;
		missionsClone[idx][key] = value; // TODO validate this works
		setMissions(missionsClone);
	}

	const missionItems = missions.map((mission, index) => {
		mission.index = index;
		mission.handleChange = handleChange;
		return rce('li', null, rce(MissionRow, mission))
	});

	return rce('div', {className: 'mission-log'},
		rce('h1', {className: 'campaign-title'}, props.campaignName, ' Campaign Log'),
		rce('div', {className: 'field-row'}, 
			rce('span', null, 'Mission'),
			rce('span', null, 'Threat Level'),
			rce('span', null, 'Rebel Upgrade'),
			rce('span', null, 'Imperial Upgrade'),
		),
		rce('ul', {className: 'mission-list'}, missionItems)
		//rce finale
	);
}

export default MissionLog;

