import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';
import ImperialTracker from './ImperialTracker';
import MissionLog from './MissionLog';
import { unstable_renderSubtreeIntoContainer } from 'react-dom';

const request = require('request');
const rce = React.createElement;

function App() {

  const url = 'http://localhost:8080';
  const options = {json: true};

  const[missionLog, setMissionLog] = useState({});

  const[imperialTracker, setImperialTracker] = useState({});
  const [ongoingAgendas, setOngoingAgendas] = useState(imperialTracker.ongoingAgendas);
  const [influence, setInfluence] = useState(imperialTracker.influence);
  const [xp, setXp] = useState(imperialTracker.xp);

  request(url, options, (error, res, body) => {
    if (error) {
      console.log(error);
    } else if (res.statusCode === 200) {
      const appData = body;
      setMissionLog(appData.missionLog);
      setImperialTracker(appData.imperialTracker);
    } else {
      console.log(res);
    }
  });




  missionLog.setMissionLog = setMissionLog;
  return missionLog.rce('div', {className: 'App'},
    rce(MissionLog, missionLog),
    rce(ImperialTracker, {
        xp: xp,
        setXp: setXp,
        influence: influence,
        setInfluence: setInfluence, 
        ongoingAgendas: ongoingAgendas,
        setOngoingAgendas: setOngoingAgendas,
      }
    )
  );
}

export default App;
