import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';
import ImperialTracker from './ImperialTracker';
import MissionLog from './MissionLog';
import { unstable_renderSubtreeIntoContainer } from 'react-dom';

const rce = React.createElement;

function App() {

  const [ongoingAgendas, setOngoingAgendas] = useState([
    {name: 'high bounty',  target: 'biv bodhrik'},
    {name: 'no escape', target: 'Garkan'}, 
    {name: 'All Too Easy', target: 'Diala Pasil'}
  ]);

  const [influence, setInfluence] = useState(0);
  const [xp, setXp] = useState(0);

  return rce('div', {className: 'App'},
    rce(MissionLog, {campaignName: 'Return to Hoth'}),
    rce(ImperialTracker, {
        xp: xp,
        setXp: setXp,
        influence: influence,
        setInfluence: setInfluence, 
        ongoingAgendas: ongoingAgendas,
        setOngoingAgendas: setOngoingAgendas,
      }
    )
  );
}

export default App;

