import React from 'react';
import './Counter.scss';

const rce = React.createElement;

// TODO check to see if pass by reference or value? may need to switch onClicks for a callback
// TODO longpress allow them to add or decrement a specific amount

/* props
* quantity
* step
*/
function Counter(props) {
	return rce('div', {className: 'counter'}, 
		rce('div', {className: 'decrement-button', onClick: () => {props.quantity -= props.step}}),
		rce('input', 
			{type: 'number', min: props.min, max: props.max, className: 'quantity'}, 
			props.quantity),
		rce('div', {className: 'increment-button', onClick: () => {props.quantity += props.step}})
	)
}

export default Counter;
