import React, { useState } from 'react';
import logo from './logo.svg';
import triangle from './triangle.svg';
import './App.css';
import './CampaignLog.scss'
import { unstable_renderSubtreeIntoContainer } from 'react-dom';

const rce = React.createElement;

function App() {
  return (
    <div className="App">
      <MissionRow missionType="Introduction" threatLevel={2} itemTiers={[1]}/>
      <MissionRow missionType="Side Mission" threatLevel={3} itemTiers={[1, 2]}/>
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

// props: triangleIconSrc, upgradeSteps, upgradeComplete, handleUpgradeChange
function UpgradeBox(props) {
  return rce('span', null,
    rce('img', {src: triangle}, null),
    rce('span', {className: 'upgrade-box'}, 
      props.upgradeSteps,
      rce('input', {type: 'checkbox', checked: props.upgradeComplete, onChange: props.handleUpgradeChange})
    )
  )
}


/* props: 
 *    missionType,
 *    threatLevel,
 *    itemTiers
 */
function MissionRow(props) {
  const [missionTitle, setMissionTitle] = useState('');
  const [missionAccomplished, setMissionAccomplished] = useState(false);
  const [rebelUpgradeComplete, setRebelUpgradeComplete] = useState(false);
  const [imperialUpgradeComplete, setImperialUpgradeComplete] = useState(false);
  const iconSrc = './logo.svg';

  function handleTitleChange(event) {
    setMissionTitle(event.target.value);
  }

  function handleMissionAccomplishedChange(event) {
    setMissionAccomplished(event.target.checked);
  }

  function handleRebelUpgradeChange(event) {
    setRebelUpgradeComplete(event.target.checked);
  }

  function handleImperialUpgradeChange(event) {
    setImperialUpgradeComplete(event.target.checked);
  }

  function printUpgradeSteps(itemTiers, rebels) {
    if(rebels) {
      let tiersStr = itemTiers.map((el, idx, arr) => {
        return idx === arr.length - 1 ? el : el + ' & ';
      }).join(' ');
      return 'Tier ' + tiersStr + ' Items, Spend XP';
    } else {
      return 'Agenda, Spend XP';
    }
  }

  return rce('div', {className: "mission-row"},
    rce('span', {className: 'cool-corners'}, 
      rce('span', {className: 'mission-type'}, props.missionType),
      rce('input', {className: 'mission-title', type: 'text', value: missionTitle, onChange: handleTitleChange}, null),
    ),
    rce('span', {className: 'threat-level cool-corners'}, 
      props.threatLevel, 
      rce('input', {type: 'checkbox', checked: missionAccomplished, onChange: handleMissionAccomplishedChange}, null),
    ),
    rce(UpgradeBox, 
      {
        triangleIconSrc: iconSrc,
        upgradeSteps: printUpgradeSteps(props.itemTiers, true),
        upgradeComplete: rebelUpgradeComplete, 
        handleUpgradeChange: handleRebelUpgradeChange
      }, null
    ),
    rce(UpgradeBox,
      {
        triangleIconSrc: iconSrc,
        upgradeSteps: printUpgradeSteps(null, false),
        upgradeComplete: imperialUpgradeComplete,
        handleUpgradeChange: handleImperialUpgradeChange
      }, null
    ),
  )
}

export default App;
